import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { addToCart, dataLoaded } from "./actions/cartActions";
import { Link } from "react-router-dom";
const url = "http://localhost:4030/product";

class ProductDetails extends Component {
  render() {
    let item = this.props.selectedItem
      return (
        <div className="card" key={item.id}>
          <div className="card-image">
            <Link
              to={`/product/${item.id}`}
              key={item.id}
              className="artist_item"
            >
              <img
                src={`data:image/jpeg;base64,${item.pic}`}
                alt={item.title}
                className="thumbnail"
              />
            </Link>
            <span className="card-title" />
            <span
              to="/"
              className="btn-floating halfway-fab waves-effect waves-light red"
              onClick={() => {
                this.handleClick(item.id);
              }}
            >
              <i className="material-icons">add</i>
            </span>
          </div>

          <div className="card-content">
            <p>{item.desc}</p>
            <p>
              <b>Price: {item.price}$</b>
            </p>
          </div>
        </div>
      );
    

    return (
      <div className="container">
        <h3 className="center">Our items</h3>
        <div className="box">{itemList}</div>
      </div>
    );
  }

  async componentDidMount() {
    const response = await axios.get(`${url}/${this.props.match.params.id}`);
    console.log(JSON.stringify(response.data));
    //console.log(JSON.stringify(response.data));
    this.props.onLoadProduct(response.data);
  }
}
const mapStateToProps = (state) => {
  return {
    selectedItem: state.selectedItem,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onLoadProduct: (selectedItem) => {
      console.log("users:" + JSON.stringify(selectedItem));
      dispatch(dataLoaded(selectedItem));
    },
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProductDetails);
