import axios from "axios";

export function loadColor() {
  return (dispatch) => {
    return axios.get("").then((response) => {
      dispatch(changeColor("#" + response.data.new_color));
    });
  };
}

export function changeColor(color) {
  return {
    type: "CHANGE_COLOR",
    color: color,
  };
}
