import React, { Component } from "react";
import { connect } from "react-redux";
import { addToCart } from "./actions/cartActions";
const users_URL = "http://localhost:4030/product/all";

class Home extends Component {
  handleClick = (id) => {
    this.props.addToCart(id);
  };

  async componentDidMount() {
    console.log("component did Mount");
    // Perform your API call, using which ever library or method you choose, i prefer axios so will demonstrate with this:
    await fetch(users_URL, {
      method: "GET",
      headers: {
        "Cache-Control": "no-cache",
      },
    })
      .then((data1) => data1.json())
      .then((response1) => {
        console.log("---->" + JSON.stringify(response1));
        //console.log("response:" + response1);

        //this.setState({ items: response1 });
        dispatch(fetchProductsSuccess(results))
        this.setState((state, props) => {
          return {
            items: response1,
          };
        });
      });
  }
  render() {
    let itemList = this.props.items.map((item) => {
      return (
        <div className="card" key={item.id}>
          <div className="card-image">
            <img src={item.img} alt={item.title} />
            <span className="card-title">{item.title}</span>
            <span
              to="/"
              className="btn-floating halfway-fab waves-effect waves-light red"
              onClick={() => {
                this.handleClick(item.id);
              }}
            >
              <i className="material-icons">add</i>
            </span>
          </div>

          <div className="card-content">
            <p>{item.desc}</p>
            <p>
              <b>Price: {item.price}$</b>
            </p>
          </div>
        </div>
      );
    });

    return (
      <div className="container">
        <h3 className="center">Our items</h3>
        <div className="box">{itemList}</div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (id) => {
      dispatch(addToCart(id));
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home);
