import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { addToCart, dataLoaded } from "./actions/cartActions";
import { Link } from "react-router-dom";
const url = "http://localhost:4030/product";

class ProductDetails extends Component {
  render() {
    //var mydata = this.state.details
    var { item } = this.props.selectedItem;
    return (
      <div>
        <div className="artist_bio">
          <div className="artist_image">
            <h3>{item.name}</h3>
            <div className="bio_text">{item.bio}</div>
          </div>
        </div>
      </div>
    );
  }

  async componentDidMount() {
    const response = await axios.get(`${url}/${this.props.match.params.id}`);
    console.log(JSON.stringify(response.data));
    //console.log(JSON.stringify(response.data));
    this.props.onLoadProduct(response.data);
  }
}
const mapStateToProps = (state) => {
  return {
    selectedItem: state.selectedItem,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onLoadProduct: (selectedItem) => {
      console.log("users:" + JSON.stringify(selectedItem));
      dispatch(dataLoaded(selectedItem));
    },
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProductDetails);
