import {
  ADD_TO_CART,
  REMOVE_ITEM,
  SUB_QUANTITY,
  ADD_QUANTITY,
  ADD_SHIPPING,
  DATA_LOADED,
} from "./action-types/cart-actions";

import axios from "axios";

export function loadColor() {
  return (dispatch) => {
    return axios.get("").then((response) => {
      dispatch(dataLoaded("#" + response.data.new_color));
    });
  };
}
//add cart action
export const addToCart = (id) => {
  return {
    type: ADD_TO_CART,
    id,
  };
};
//remove item action
export const removeItem = (id) => {
  return {
    type: REMOVE_ITEM,
    id,
  };
};
//subtract qt action
export const subtractQuantity = (id) => {
  return {
    type: SUB_QUANTITY,
    id,
  };
};
//add qt action
export const addQuantity = (id) => {
  return {
    type: ADD_QUANTITY,
    id,
  };
};
export const addShipping = (id) => {
  return {
    type: ADD_SHIPPING,
    id,
  };
};
export function dataLoaded(items) {
  return {
    type: DATA_LOADED,
    items: items,
  };
}
