import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { addToCart, dataLoaded } from "./actions/cartActions";

const url = "http://localhost:4030/product";

class ProductDetails extends Component {
  render() {
    //var mydata = this.state.details
    var { details } = this.state;
    return (
      <div>
        <div className="artist_bio">
          <div className="artist_image">
            <span
              style={{
                background: `url('/images/covers/${details.cover}.jpg')`,
              }}
            />
            <h3>{details.name}Details Page</h3>
            <div className="bio_text">{details.bio}</div>
          </div>
        </div>
      </div>
    );
  }

  async componentDidMount() {
    const response = await axios.get(`${url}/${this.props.match.params.id}`);
    console.log(JSON.stringify(response.data));
    console.log(JSON.stringify(response.data));
    this.props.onLoadProduct(response.data);
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (id) => {
      dispatch(addToCart(id));
    },
    onLoadProduct: (selectedItem) => {
      console.log("users:" + JSON.stringify(selectedItem));
      dispatch(dataLoaded(selectedItem));
    },
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProductDetails);
