import React, { Component } from "react";
import axios from "axios";
import Albums from "./Albums";

const url = "http://localhost:8900/artists";

class ProductDetails extends Component {
  constructor() {
    super();

    this.state = {
      details: "",
    };
  }

  render() {
    //var mydata = this.state.details
    var { details } = this.state;
    return (
      <div>
        <div className="artist_bio">
          <div className="artist_image">
            <span
              style={{
                background: `url('/images/covers/${details.cover}.jpg')`,
              }}
            />
            <h3>{details.name}Details Page</h3>
            <div className="bio_text">{details.bio}</div>
            <Albums albumList={details.albums} />
          </div>
        </div>
      </div>
    );
  }

  async componentDidMount() {
    const response = await axios.get(`${url}/${this.props.match.params.id}`);
    this.setState({ details: response.data });
  }
}

export default ProductDetails;
