import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { addToCart, productLoad } from "./actions/cartActions";
import { Link } from "react-router-dom";
const url = "http://localhost:4030/product";

class ProductDetails extends Component {
  
  render() {
    let item = this.props.selectedItem;
    return (
      <div className="card" key={this.props.selectedItem.id}>
        <div className="card-image">
          <img
            src={`data:image/jpeg;base64,${this.props.selectedItem.pic}`}
            alt={this.props.selectedItem.title}
            className="thumbnail"
          />

          <span
            to="/"
            className="btn-floating halfway-fab waves-effect waves-light red"
            onClick={() => {
              this.handleClick(this.props.selectedItem.id);
            }}
          >
            <i className="material-icons">add</i>
          </span>
        </div>

        <div className="card-content">
          <p>{this.props.selectedItem.desc}</p>
          <p>
            <b>Price: {this.props.selectedItem.price}$</b>
          </p>
        </div>
      </div>
    );
  }

  
}
const mapStateToProps = (state) => {
  return {
    selectedItem: state.selectedItem,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onLoadProduct: (selectedItem) => {
      console.log("users:" + JSON.stringify(selectedItem));
      dispatch(productLoad(selectedItem));
    },
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProductDetails);
